import fs from 'fs';
import path from 'path';
import * as pkg from '../package.json';
import program from 'commander';
import {Server} from './server';
import yaml from 'js-yaml';

/**
 * Represents an application providing functionalities specific to console requests.
 */
export class Application {

  /**
   * Value indicating whether the application runs in debug mode.
   * @type {boolean}
   */
  get debug() {
    return this.env == 'development' || this.env == 'test';
  }

  /**
   * The application environment.
   * @type {string}
   */
  get env() {
    return 'NODE_ENV' in process.env ? process.env.NODE_ENV : 'production';
  }

  /**
   * Loads the application configuration from the file system.
   * @param {object} args The command line arguments.
   * @return {Promise<object[]>} An array of objects containing the settings of one or several reverse proxy instances.
   */
  async loadConfig(args) {
    if (!args.config) return [{
      address: args.address,
      port: args.port,
      target: args.target
    }];

    const readFile = file => new Promise(resolve => fs.readFile(file, 'utf8', (err, data) => resolve(err ? '' : data)));
    return this._parseConfig(await readFile(path.resolve(args.config)));
  }

  /**
   * Prints the specified message, with a timestamp and a new line, to the standard output.
   * @param {string|function} message The message to be logged. If it's a function, the message is the result of the function call.
   */
  log(message) {
    if (!program.silent) {
      let now = new Date().toISOString();
      let text = typeof message == 'function' ? message() : message;
      console.log(`[${now}] ${text}`);
    }
  }

  /**
   * Runs the application.
   * @return {Promise} Completes when the reverse proxy has been started.
   */
  async run() {
    // Parse the command line arguments.
    const format = {
      asInteger: value => parseInt(value, 10),
      asIntegerIfNumeric: value => /^\d+$/.test(value) ? parseInt(value, 10) : value
    };

    program._name = 'reverse-proxy';
    program
      .description('Simple reverse proxy server supporting WebSockets.')
      .version(pkg.version, '-v, --version')
      .option('-a, --address <address>', `address that the reverse proxy should run on [${Server.DEFAULT_ADDRESS}]`, Server.DEFAULT_ADDRESS)
      .option('-p, --port <port>', `port that the reverse proxy should run on [${Server.DEFAULT_PORT}]`, format.asInteger, Server.DEFAULT_PORT)
      .option('-t, --target <target>', 'location of the server the proxy will target', format.asIntegerIfNumeric)
      .option('-c, --config <path>', 'location of the configuration file for the reverse proxy')
      .option('-u, --user <user>', 'user to drop privileges to once server socket is bound', format.asIntegerIfNumeric)
      .option('--silent', 'silence the log output from the reverse proxy');

    program.parse(process.argv);
    if (!program.config && !program.target) program.help();

    // Start the proxy server.
    try {
      let config = await this.loadConfig(program);
      if (!config.length) throw new Error('Unable to find any configuration for the reverse proxy.');

      await this.startServers(config.map(options => new Server(options)));
      if (program.user) this.setUser(program.user);
    }

    catch (err) {
      console.error(this.debug ? err.stack : err.message);
      process.exit(1);
    }

    return null;
  }

  /**
   * Sets the user identity of the application process.
   * @param {number|string} userId The user identifier.
   */
  setUser(userId) {
    if (typeof process.setuid != 'function') this.log('Changing the process user is not supported on this platform.');
    else {
      this.log(`Drop user privileges to: ${userId}`);
      process.setuid(userId);
    }
  }

  /**
   * Starts the specified reverse proxy instances.
   * @param {Server[]} servers The list of servers to start.
   * @return {Promise} Completes when all servers have been started.
   */
  async startServers(servers) {
    return Promise.all(servers.map(server => {
      server.onClose.subscribe(() => this.log(`Reverse proxy instance on ${server.address}:${server.port} closed`));
      server.onError.subscribe(err => this.log(this.debug ? err.stack : err.message));
      server.onListen.subscribe(() => this.log(`Reverse proxy instance listening on ${server.address}:${server.port}`));

      server.onRequest.subscribe(req => {
        let ipAddress = req.connection.remoteAddress;
        let userAgent = req.headers['user-agent'];
        this.log(`${ipAddress} - ${req.headers.host} - "${req.method} ${req.url} HTTP/${req.httpVersion}" "${userAgent}"`);
      });

      return server.listen();
    }));
  }

  /**
   * Parses the specified configuration.
   * @param {string} data A string specifying the application configuration.
   * @return {object[]} An array of objects corresponding to the parsed configuration.
   */
  _parseConfig(data) {
    data = data.trim();
    if (!data.length) throw new Error('Invalid configuration data.');

    let config = [];
    let parser = options => {
      if (!('routes' in options) && !('target' in options))
        throw new Error('You must provide at least a target or a route table.');

      if (!('address' in options)) options.address = program.address;
      if (!('port' in options)) options.port = program.port;

      if ('ssl' in options) {
        /* eslint-disable no-sync */
        if ('ca' in options.ssl) options.ssl.ca = fs.readFileSync(options.ssl.ca);
        if ('cert' in options.ssl) options.ssl.cert = fs.readFileSync(options.ssl.cert);
        if ('key' in options.ssl) options.ssl.key = fs.readFileSync(options.ssl.key);
        if ('pfx' in options.ssl) options.ssl.pfx = fs.readFileSync(options.ssl.pfx);
        /* eslint-enable no-sync */
      }

      config.push(options);
    };

    let firstChar = data[0];
    let lastChar = data[data.length - 1];

    let isJson = (firstChar == '[' || firstChar == '{') && (lastChar == ']' || lastChar == '}');
    if (!isJson) yaml.safeLoadAll(data, parser);
    else {
      let options = JSON.parse(data);
      if (!Array.isArray(options)) options = [options];
      options.forEach(parser);
    }

    return config;
  }
}

import http from 'http';
import https from 'https';
import httpProxy from 'http-proxy';
import {Subject} from 'rxjs';

/**
 * Acts as an intermediary for requests from clients seeking resources from other servers.
 */
export class Server {

  /**
   * The default address that the server is listening on.
   * @type {string}
   */
  static get DEFAULT_ADDRESS() {
    return '0.0.0.0';
  }

  /**
   * The default port that the server is listening on.
   * @type {number}
   */
  static get DEFAULT_PORT() {
    return 3000;
  }

  /**
   * Initializes a new instance of the class.
   * @param {object} [options] An object specifying the server settings.
   */
  constructor(options = {}) {

    /**
     * The underlying HTTP(S) service listening for requests.
     * @type {http.Server|https.Server}
     */
    this._httpService = null;

    /**
     * The handler of "close" events.
     * @type {Subject}
     */
    this._onClose = new Subject();

    /**
     * The handler of "error" events.
     * @type {Subject<Error>}
     */
    this._onError = new Subject();

    /**
     * The handler of "listen" events.
     * @type {Subject}
     */
    this._onListen = new Subject();

    /**
     * The handler of "request" events.
     * @type {Subject<http.IncomingMessage>}
     */
    this._onRequest = new Subject();

    /**
     * The server settings.
     * @type {object}
     */
    this._options = options;
    if (!('routes' in this._options)) this._options.routes = {};
    if ('target' in this._options) this._options.routes['*'] = this._options.target;

    /**
     * The underlying proxy service providing custom application logic.
     * @type {httpProxy.Server}
     */
    this._proxyService = httpProxy.createProxyServer(this._options.proxy || {});
    this._proxyService.on('error', this._onRequestError.bind(this));

    // Normalize the routing table.
    let routes = this._options.routes;
    for (let host in routes) {
      let target = routes[host];
      if (typeof target == 'number') routes[host] = `http://127.0.0.1:${target}`;
      else if (!/^https?:/i.test(target)) routes[host] = `http://${target}`;
    }
  }

  /**
   * The address that the server is listening on.
   * @type {string}
   */
  get address() {
    return typeof this._options.address == 'string' ? this._options.address : Server.DEFAULT_ADDRESS;
  }

  /**
   * Value indicating whether the server is currently listening.
   * @type {boolean}
   */
  get listening() {
    return Boolean(this._httpService && this._httpService.listening);
  }

  /**
   * The stream of "close" events.
   * @type {Observable}
   */
  get onClose() {
    return this._onClose.asObservable();
  }

  /**
   * The stream of "error" events.
   * @type {Observable<Error>}
   */
  get onError() {
    return this._onError.asObservable();
  }

  /**
   * The stream of "listen" events.
   * @type {Observable}
   */
  get onListen() {
    return this._onListen.asObservable();
  }

  /**
   * The stream of "request" events.
   * @type {Observable<http.IncomingMessage>}
   */
  get onRequest() {
    return this._onRequest.asObservable();
  }

  /**
   * The port that the server is listening on.
   * @type {number}
   */
  get port() {
    return typeof this._options.port == 'number' ? this._options.port : Server.DEFAULT_PORT;
  }

  /**
   * Stops the server from accepting new connections. It does nothing if the server is already closed.
   * @return {Promise} Completes when the server is finally closed.
   * @emits {*} The "close" event.
   */
  async close() {
    if (this.listening) await new Promise(resolve => this._httpService.close(() => {
      this._httpService = null;
      this._onClose.next();
      resolve();
    }));

    return null;
  }

  /**
   * Begin accepting connections. It does nothing if the server is already started.
   * @param {number} [port] The port that the server should run on.
   * @param {string} [address] The address that the server should run on.
   * @return {Promise<number>} The port that the server is running on.
   * @emits {*} The "listen" event.
   */
  async listen(port = -1, address = '') {
    if (!this.listening) {
      this._httpService = 'ssl' in this._options ?
        https.createServer(this._options.ssl, this._onHTTPRequest.bind(this)) :
        http.createServer(this._onHTTPRequest.bind(this));

      this._httpService.on('error', err => this._onError.next(err));
      this._httpService.on('upgrade', this._onWSRequest.bind(this));

      await new Promise(resolve => this._httpService.listen(port > 0 ? port : this.port, address.length ? address : this.address, () => {
        let socket = this._httpService.address();
        this._options.address = socket.address;
        this._options.port = socket.port;

        this._onListen.next();
        resolve();
      }));
    }

    return this._options.port;
  }

  /**
   * Gets the host name contained in the headers of the specified request.
   * @param {http.IncomingMessage} req The request sent by the client.
   * @return {string} The host name provided by the specified request, or `*` if the host name could not be determined.
   */
  _getHostName(req) {
    let headers = req.headers;
    if (!('host' in headers)) return '*';

    let index = headers.host.indexOf(':');
    return index < 0 ? headers.host : headers.host.substr(0, index);
  }

  /**
   * Handles an HTTP request to a target.
   * @param {http.IncomingMessage} req The request sent by the client.
   * @param {http.ServerResponse} res The response sent by the server.
   * @emits {http.IncomingMessage} The "request" event.
   */
  _onHTTPRequest(req, res) {
    this._onRequest.next(req);

    let hostName = this._getHostName(req);
    let host = hostName in this._options.routes ? hostName : '*';

    if (host in this._options.routes) this._proxyService.web(req, res, {target: this._options.routes[host]});
    else this._sendStatus(res, 404);
  }

  /**
   * Handles the error emitted if a request to a target fails.
   * @param {Error} err The emitted error event.
   * @param {http.IncomingMessage} req The request sent by the client.
   * @param {http.ServerResponse} res The response sent by the server.
   * @emits {Error} The "error" event.
   */
  _onRequestError(err, req, res) {
    this._onError.next(err);
    this._sendStatus(res, 502);
  }

  /**
   * Handles a WebSocket request to a target.
   * @param {http.IncomingMessage} req The request sent by the client.
   * @param {net.Socket} socket The network socket between the server and client.
   * @param {Buffer} head The first packet of the upgraded stream.
   */
  _onWSRequest(req, socket, head) {
    let hostName = this._getHostName(req);
    let host = hostName in this._options.routes ? hostName : '*';
    if (host in this._options.routes) this._proxyService.ws(req, socket, head, {target: this._options.routes[host]});
  }

  /**
   * Sends an HTTP status code and terminates the specified server response.
   * @param {http.ServerResponse} res The server response.
   * @param {number} statusCode The HTTP status code to send.
   */
  _sendStatus(res, statusCode) {
    let message = http.STATUS_CODES[statusCode];
    res.writeHead(statusCode, {
      'Content-Length': Buffer.byteLength(message),
      'Content-Type': 'text/plain'
    });

    res.end(message);
  }
}

'use strict';

import {expect} from 'chai';
import {Application} from '../src/index';

/**
 * @test {Application}
 */
describe('Application', () => {

  /**
   * @test {Application#debug}
   */
  describe('#debug', () => {
    it('should be `false` in production environment', () => {
      process.env.NODE_ENV = 'production';
      expect(new Application().debug).to.be.false;
    });

    it('should be `true` in development environment', () => {
      process.env.NODE_ENV = 'development';
      expect(new Application().debug).to.be.true;
    });
  });

  /**
   * @test {Application#env}
   */
  describe('#env', () => {
    it('should be "production" if the NODE_ENV environment variable is not set', () => {
      delete process.env.NODE_ENV;
      expect(new Application().env).to.equal('production');
    });

    it('should equal the value of `NODE_ENV` environment variable when it is set', () => {
      process.env.NODE_ENV = 'development';
      expect(new Application().env).to.equal('development');
    });
  });

  /**
   * @test {Application#loadConfig}
   */
  describe('#loadConfig()', () => {
    it('should return an array of objects corresponding to the ones specified on the command line arguments', async () => {
      let config = await new Application().loadConfig({port: 80, target: 3000});
      expect(config).to.be.an('array').and.have.lengthOf(1);
      expect(config[0].port).to.equal(80);
      expect(config[0].target).to.equal(3000);
    });

    it('should return an array of objects corresponding to the ones specified in the JSON configuration', async () => {
      let config = await new Application().loadConfig({config: `${__dirname}/../example/json/basic_standalone.json`});
      expect(config).to.be.an('array').and.have.lengthOf(1);
      expect(config[0].port).to.equal(80);
      expect(config[0].target).to.equal(3000);
    });

    it('should return an array of objects corresponding to the ones specified in the YAML configuration', async () => {
      let config = await new Application().loadConfig({config: `${__dirname}/../example/yaml/basic_standalone.yaml`});
      expect(config).to.be.an('array').and.have.lengthOf(1);
      expect(config[0].port).to.equal(80);
      expect(config[0].target).to.equal(3000);
    });
  });

  /**
   * @test {Application#_parseConfig}
   */
  describe('#_parseConfig()', () => {
    it('should throw an error if the parsed JSON configuration has no `routes` and no `target` properties', async () => {
      try {
        await new Application()._parseConfig('{"port": 80}');
        expect(true).to.not.be.ok;
      }

      catch (err) {
        expect(true).to.be.ok;
      }
    });

    it('should completes with an array if the parsed JSON configuration is valid', async () => {
      let config = await new Application()._parseConfig('{"port": 80, "target": 3000}');
      expect(config).to.be.an('array').and.have.lengthOf(1);
      expect(config[0]).to.be.an('object');
      expect(config[0].port).to.equal(80);
      expect(config[0].target).to.equal(3000);
    });

    it('should throw an error if the parsed YAML configuration has no `routes` and no `target` properties', async () => {
      try {
        await new Application()._parseConfig('port: 80');
        expect(true).to.not.be.ok;
      }

      catch (err) {
        expect(true).to.be.ok;
      }
    });

    it('should completes with an array if the parsed YAML configuration is valid', async () => {
      let config = await new Application()._parseConfig('port: 80\ntarget: 3000');
      expect(config).to.be.an('array').and.have.lengthOf(1);
      expect(config[0]).to.be.an('object');
      expect(config[0].port).to.equal(80);
      expect(config[0].target).to.equal(3000);
    });
  });
});

'use strict';

import {expect} from 'chai';
import {Observable, Subject} from 'rxjs';
import {Server} from '../src/index';

/**
 * @test {Server}
 */
describe('Server', () => {

  /**
   * @test {Server#address}
   */
  describe('#address', () => {
    it('should have an "any IPv4" address as the default address', () => {
      expect(new Server().address).to.equal(Server.DEFAULT_ADDRESS);
    });

    it('should have the same host as the specified one', () => {
      expect(new Server({address: 'localhost'}).address).to.equal('localhost');
    });
  });

  /**
   * @test {Server#listening}
   */
  describe('#listening', () => {
    it('should return whether the server is listening', async () => {
      let server = new Server({address: '127.0.0.1', port: 0});
      expect(server.listening).to.be.false;
      await server.listen();
      expect(server.listening).to.be.true;
      await server.close();
      expect(server.listening).to.be.false;
    });
  });

  /**
   * @test {Server#onClose}
   */
  describe('#onClose', () => {
    it('should return an Observable instead of the underlying Subject', () => {
      let stream = new Server().onClose;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onError}
   */
  describe('#onError', () => {
    it('should return an Observable instead of the underlying Subject', () => {
      let stream = new Server().onError;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onListen}
   */
  describe('#onListen', () => {
    it('should return an Observable instead of the underlying Subject', () => {
      let stream = new Server().onListen;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onRequest}
   */
  describe('#onRequest', () => {
    it('should return an Observable instead of the underlying Subject', () => {
      let stream = new Server().onRequest;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#port}
   */
  describe('#port', () => {
    it('should have 3000 as the default port', () => {
      expect(new Server().port).to.equal(Server.DEFAULT_PORT);
    });

    it('should have the same port as the specified one', () => {
      expect(new Server({port: 8080}).port).to.equal(8080);
    });
  });

  /**
   * @test {Server#_getHostName}
   */
  describe('#_getHostName()', () => {
    it('it should return "*" if there is no "Host" header in the request', () => {
      expect(new Server()._getHostName({headers: {}})).to.equal('*');
    });

    it('it should return the "Host" header found in the request, without the port number', () => {
      expect(new Server()._getHostName({headers: {host: 'belin.io:8080'}})).to.equal('belin.io');
    });
  });
});
