'use strict';

const program = require('commander');
const {readFile} = require('fs');
const {safeLoadAll: loadYAML} = require('js-yaml');
const morgan = require('morgan');
const {Observable} = require('rxjs');

const {version: pkgVersion} = require('../package.json');
const {Server} = require('./server');

/**
 * Represents an application providing functionalities specific to console requests.
 */
exports.Application = class Application {

  /**
   * The format used for logging the requests.
   * @type {string}
   */
  static get LOG_FORMAT() {
    return ':req[host] :remote-addr - :remote-user [:date[clf]] ":method :url HTTP/:http-version" :status :res[content-length] ":referrer" ":user-agent"';
  }

  /**
   * Initializes a new instance of the class.
   */
  constructor() {

    /**
     * The proxy servers managed by this application.
     * @type {Server[]}
     */
    this.servers = [];
  }

  /**
   * Value indicating whether the application runs in debug mode.
   * @type {boolean}
   */
  get debug() {
    return ['development', 'test'].includes(this.env);
  }

  /**
   * The application environment.
   * @type {string}
   */
  get env() {
    return 'NODE_ENV' in process.env ? process.env.NODE_ENV : 'development';
  }

  /**
   * Initializes the application.
   * @param {object} [args] The command line arguments.
   * @return {Observable} Completes when the initialization is over.
   */
  init(args = {}) {
    let observable;
    if (typeof args.config == 'string') {
      const loadConfig = Observable.bindNodeCallback(readFile);
      observable = loadConfig(args.config, 'utf8').mergeMap(data => this._parseConfig(data));
    }
    else observable = Observable.of([new Server({
      address: args.address,
      port: args.port,
      target: args.target
    })]);

    return observable.map(servers => this.servers = servers);
  }

  /**
   * Runs the application.
   * @return {Observable} Completes when the reverse proxy has been started.
   */
  run() {
    // Parse the command line arguments.
    const format = {
      asInteger: value => Number.parseInt(value, 10),
      asIntegerIfNumeric: value => /^\d+$/.test(value) ? Number.parseInt(value, 10) : value
    };

    program.name('reverse-proxy')
      .description('Simple reverse proxy server supporting WebSockets.')
      .version(pkgVersion, '-v, --version')
      .option('-a, --address <address>', `address that the reverse proxy should run on [${Server.DEFAULT_ADDRESS}]`, Server.DEFAULT_ADDRESS)
      .option('-p, --port <port>', `port that the reverse proxy should run on [${Server.DEFAULT_PORT}]`, format.asInteger, Server.DEFAULT_PORT)
      .option('-t, --target <target>', 'location of the server the proxy will target', format.asIntegerIfNumeric)
      .option('-c, --config <path>', 'location of the configuration file for the reverse proxy')
      .option('-u, --user <user>', 'user to drop privileges to once server socket is bound', format.asIntegerIfNumeric)
      .option('--silent', 'silence the log output from the reverse proxy')
      .parse(process.argv);

    if (!program.config && !program.target) program.help();

    // Start the proxy server.
    return this.init(program)
      .mergeMap(() => {
        if (!this.servers.length) return Observable.throw(new Error('Unable to find any configuration for the reverse proxy.'));
        return this._startServers();
      })
      .do({complete: () => {
        if (program.user) this._setUser(program.user);
      }});
  }

  /**
   * Parses the specified configuration.
   * @param {string} data A string specifying the application configuration.
   * @return {Observable<Server[]>} The server instances corresponding to the parsed configuration.
   */
  _parseConfig(data) {
    data = data.trim();
    if (!data.length) return Observable.throw(new Error('Invalid configuration data.'));

    /* eslint-disable no-extra-parens */
    let config;
    let firstChar = data[0];
    let lastChar = data[data.length - 1];
    let isJson = (firstChar == '[' && lastChar == ']') || (firstChar == '{' && lastChar == '}');
    /* eslint-enable no-extra-parens */

    try {
      if (!isJson) {
        config = [];
        loadYAML(data, options => config.push(options));
      }
      else {
        config = JSON.parse(data);
        if (!Array.isArray(config)) config = [config];
      }

      if (!config.every(value => typeof value == 'object' && value))
        throw new Error('Invalid configuration format.');
    }

    catch (err) {
      return Observable.throw(err);
    }

    const loadCert = Observable.bindNodeCallback(readFile);
    return Observable.from(config).mergeMap(options => {
      if (!('routes' in options) && !('target' in options))
        return Observable.throw(new Error('You must provide at least a target or a routing table.'));

      if (!('address' in options)) options.address = program.address;
      if (!('port' in options)) options.port = program.port;
      if (!('ssl' in options)) return Observable.of(new Server(options));

      let keys = ['ca', 'cert', 'key', 'pfx'].filter(key => key in options.ssl);
      let observables = keys.map(key => loadCert(options.ssl[key]));
      return Observable.zip(...observables)
        .do(certs => { for (let i = 0; i < keys.length; i++) options.ssl[keys[i]] = certs[i]; })
        .map(() => new Server(options));
    }).toArray();
  }

  /**
   * Sets the user identity of the application process.
   * @param {number|string} userId The user identifier.
   */
  _setUser(userId) {
    if (typeof process.setuid != 'function')
      console.error('Changing the process user is not supported on this platform.');
    else {
      console.log(`Drop user privileges to "${userId}"`);
      process.setuid(userId);
    }
  }

  /**
   * Starts the reverse proxy instances.
   * @return {Observable} Completes when all servers have been started.
   */
  _startServers() {
    let done = () => {};
    let logger = morgan(this.debug ? 'dev' : Application.LOG_FORMAT);

    return Observable.merge(...this.servers.map(server => {
      server.onClose.subscribe(() => console.log(`Reverse proxy instance on ${server.address}:${server.port} closed`));
      server.onError.subscribe(error => console.error(this.debug ? error : error.message));
      server.onListening.subscribe(() => console.log(`Reverse proxy instance listening on ${server.address}:${server.port}`));
      if (!program.silent) server.onRequest.subscribe(({request, response}) => logger(request, response, done));

      return server.listen();
    }));
  }
};

'use strict';

const {createServer, STATUS_CODES} = require('http');
const {createServer: createSecureServer} = require('https');
const {createProxyServer} = require('http-proxy');
const {Observable, Subject} = require('rxjs');

/**
 * Acts as an intermediary for requests from clients seeking resources from other servers.
 */
exports.Server = class Server {

  /**
   * The default address that the server is listening on.
   * @type {string}
   */
  static get DEFAULT_ADDRESS() {
    return '0.0.0.0';
  }

  /**
   * The default port that the server is listening on.
   * @type {number}
   */
  static get DEFAULT_PORT() {
    return 3000;
  }

  /**
   * Initializes a new instance of the class.
   * @param {object} [options] An object specifying the server settings.
   */
  constructor(options = {}) {

    /**
     * The routing table.
     * @type {Map}
     */
    this.routes = new Map;
    if ('routes' in options) for (let host in options.routes) this.routes.set(host, this._normalizeRoute(options.routes[host]));
    if ('target' in options) this.routes.set('*', this._normalizeRoute(options.target));

    /**
     * The underlying HTTP(S) service listening for requests.
     * @type {http~Server|https~Server}
     */
    this._httpService = null;

    /**
     * The handler of "close" events.
     * @type {Subject}
     */
    this._onClose = new Subject();

    /**
     * The handler of "error" events.
     * @type {Subject<Error>}
     */
    this._onError = new Subject();

    /**
     * The handler of "listening" events.
     * @type {Subject}
     */
    this._onListening = new Subject();

    /**
     * The handler of "request" events.
     * @type {Subject<object>}
     */
    this._onRequest = new Subject();

    /**
     * The server settings.
     * @type {object}
     */
    this._options = {
      address: typeof options.address == 'string' ? options.address : Server.DEFAULT_ADDRESS,
      port: Number.isInteger(options.port) ? options.port : Server.DEFAULT_PORT,
      proxy: typeof options.proxy == 'object' && options.proxy ? options.proxy : null,
      ssl: typeof options.ssl == 'object' && options.ssl ? options.ssl : null
    };

    /**
     * The underlying proxy service providing custom application logic.
     * @type {ProxyServer}
     */
    this._proxyService = null;
  }

  /**
   * The address that the server is listening on.
   * @type {string}
   */
  get address() {
    return this.listening ? this._httpService.address().address : this._options.address;
  }

  /**
   * Value indicating whether the server is currently listening.
   * @type {boolean}
   */
  get listening() {
    return Boolean(this._httpService && this._httpService.listening);
  }

  /**
   * The stream of "close" events.
   * @type {Observable}
   */
  get onClose() {
    return this._onClose.asObservable();
  }

  /**
   * The stream of "error" events.
   * @type {Observable<Error>}
   */
  get onError() {
    return this._onError.asObservable();
  }

  /**
   * The stream of "listening" events.
   * @type {Observable}
   */
  get onListening() {
    return this._onListening.asObservable();
  }

  /**
   * The stream of "request" events.
   * @type {Observable<object>}
   */
  get onRequest() {
    return this._onRequest.asObservable();
  }

  /**
   * The port that the server is listening on.
   * @type {number}
   */
  get port() {
    return this.listening ? this._httpService.address().port : this._options.port;
  }

  /**
   * Stops the server from accepting new connections. It does nothing if the server is already closed.
   * @return {Observable} Completes when the server is finally closed.
   * @emits {*} The "close" event.
   */
  close() {
    if (!this.listening) return Observable.of(null);

    const close = Observable.bindCallback(this._httpService.close.bind(this._httpService));
    return close().do(() => {
      this._httpService = null;
      this._proxyService = null;
      this._onClose.next();
    });
  }

  /**
   * Begin accepting connections. It does nothing if the server is already started.
   * @param {number} [port] The port that the server should run on.
   * @param {string} [address] The address that the server should run on.
   * @return {Observable<number>} The port that the server is running on.
   * @emits {*} The "listening" event.
   */
  listen(port = this.port, address = this.address) {
    if (this.listening) return Observable.of(this.port);

    let requestHandler = this._onHTTPRequest.bind(this);
    this._httpService = this._options.ssl ? createSecureServer(this._options.ssl, requestHandler) : createServer(requestHandler);
    this._httpService.on('error', error => this._onError.next(error));
    this._httpService.on('upgrade', this._onWSRequest.bind(this));

    this._proxyService = createProxyServer(this._options.proxy);
    this._proxyService.on('error', this._onRequestError.bind(this));

    const listen = Observable.bindCallback(this._httpService.listen.bind(this._httpService));
    return listen(port, address).map(() => {
      this._onListening.next();
      return this.port;
    });
  }

  /**
   * Gets the host name contained in the headers of the specified request.
   * @param {http~IncomingMessage} request The request sent by the client.
   * @return {string} The host name provided by the specified request, or `*` if the host name could not be determined.
   */
  _getHostname(request) {
    let headers = request.headers;
    if (!('host' in headers)) return '*';

    let index = headers.host.indexOf(':');
    return index < 0 ? headers.host : headers.host.substr(0, index);
  }

  /**
   * Normalizes the specified route.
   * @param {*} route The route to normalize.
   * @return {object} The normalized route.
   * @throws {Error} The route has an invalid format.
   */
  _normalizeRoute(route) {
    let value = typeof route == 'object' && route ? route : {uri: route};

    switch (typeof value.uri) {
      case 'number':
        value.uri = `http://127.0.0.1:${value.uri}`;
        break;

      case 'string':
        if (!/^https?:/i.test(value.uri)) value.uri = `http://${value.uri}`;
        break;

      default:
        throw new Error('The route has an invalid format.');
    }

    if (typeof value.headers != 'object' || !value.headers) value.headers = {};
    else {
      let map = {};
      for (let key in value.headers) map[key.toLowerCase()] = value.headers[key];
      value.headers = map;
    }

    return value;
  }

  /**
   * Handles an HTTP request to a target.
   * @param {http~IncomingMessage} request The request sent by the client.
   * @param {http~ServerResponse} response The response sent by the server.
   * @emits {object} The "request" event.
   */
  _onHTTPRequest(request, response) {
    this._onRequest.next({request, response});

    let hostname = this._getHostname(request);
    let pattern = this.routes.has(hostname) ? hostname : '*';
    if (!this.routes.has(pattern)) this._sendStatus(response, 404);
    else {
      let target = this.routes.get(pattern);
      Object.assign(request.headers, target.headers);
      this._proxyService.web(request, response, {target: target.uri});
    }
  }

  /**
   * Handles the error emitted if a request to a target fails.
   * @param {Error} error The emitted error event.
   * @param {http~IncomingMessage} request The request sent by the client.
   * @param {http~ServerResponse} response The response sent by the server.
   * @emits {Error} The "error" event.
   */
  _onRequestError(error, request, response) {
    this._onError.next(error);
    this._sendStatus(response, 502);
  }

  /**
   * Handles a WebSocket request to a target.
   * @param {http~IncomingMessage} request The request sent by the client.
   * @param {net~Socket} socket The network socket between the server and client.
   * @param {Buffer} head The first packet of the upgraded stream.
   */
  _onWSRequest(request, socket, head) {
    let hostname = this._getHostname(request);
    let pattern = this.routes.has(hostname) ? hostname : '*';
    if (this.routes.has(pattern)) {
      let target = this.routes.get(pattern);
      Object.assign(request.headers, target.headers);
      this._proxyService.ws(request, socket, head, {target: target.uri});
    }
  }

  /**
   * Sends an HTTP status code and terminates the specified server response.
   * @param {http~ServerResponse} response The server response.
   * @param {number} statusCode The HTTP status code to send.
   */
  _sendStatus(response, statusCode) {
    let message = STATUS_CODES[statusCode];
    response.writeHead(statusCode, {
      'Content-Length': Buffer.byteLength(message),
      'Content-Type': 'text/plain; charset=utf-8'
    });

    response.end(message);
  }
};

'use strict';

const {expect} = require('chai');
const {Application, Server} = require('../lib');

/**
 * @test {Application}
 */
describe('Application', () => {

  /**
   * @test {Application#debug}
   */
  describe('#debug', () => {
    it('should be `false` in production environment', () => {
      process.env.NODE_ENV = 'production';
      expect((new Application).debug).to.be.false;
    });

    it('should be `true` in development environment', () => {
      process.env.NODE_ENV = 'development';
      expect((new Application).debug).to.be.true;
    });
  });

  /**
   * @test {Application#env}
   */
  describe('#env', () => {
    it('should be "development" if the NODE_ENV environment variable is not set', () => {
      delete process.env.NODE_ENV;
      expect((new Application).env).to.equal('development');
    });

    it('should equal the value of `NODE_ENV` environment variable when it is set', () => {
      process.env.NODE_ENV = 'production';
      expect((new Application).env).to.equal('production');
    });
  });

  /**
   * @test {Application#init}
   */
  describe('#init()', () => {
    it('should initialize the `servers` property from the command line arguments', done => {
      let app = new Application;
      app.init({port: 80, target: 3000}).subscribe(() => {
        expect(app.servers).to.be.an('array').and.have.lengthOf(1);
        expect(app.servers[0].port).to.equal(80);
      }, done, done);
    });

    it('should initialize the `servers` property from the JSON configuration', done => {
      let app = new Application;
      app.init({config: 'example/json/basic_standalone.json'}).subscribe(() => {
        expect(app.servers).to.be.an('array').and.have.lengthOf(1);
        expect(app.servers[0].port).to.equal(80);
      }, done, done);
    });

    it('should initialize the `servers` property from the YAML configuration', done => {
      let app = new Application;
      app.init({config: 'example/yaml/basic_standalone.yaml'}).subscribe(() => {
        expect(app.servers).to.be.an('array').and.have.lengthOf(1);
        expect(app.servers[0].port).to.equal(80);
      }, done, done);
    });
  });

  /**
   * @test {Application#_parseConfig}
   */
  describe('#_parseConfig()', () => {
    it('should throw an error if the configuration has an invalid format', done => {
      (new Application)._parseConfig('"FooBar"').subscribe({
        complete: () => done(new Error('Error not thrown.')),
        error: () => done()
      });
    });

    it('should throw an error if the parsed JSON configuration has no `routes` and no `target` properties', done => {
      (new Application)._parseConfig('{"port": 80}').subscribe({
        complete: () => done(new Error('Error not thrown.')),
        error: () => done()
      });
    });

    it('should throw an error if the parsed YAML configuration has no `routes` and no `target` properties', done => {
      (new Application)._parseConfig('port: 80').subscribe({
        complete: () => done(new Error('Error not thrown.')),
        error: () => done()
      });
    });

    it('should completes with an array if the parsed JSON configuration is valid', done => {
      (new Application)._parseConfig('{"port": 80, "target": 3000}').subscribe(config => {
        expect(config).to.be.an('array').and.have.lengthOf(1);
        expect(config[0]).to.be.instanceof(Server);
        expect(config[0].port).to.equal(80);
      }, done, done);
    });

    it('should completes with an array if the parsed YAML configuration is valid', done => {
      (new Application)._parseConfig('port: 80\ntarget: 3000').subscribe(config => {
        expect(config).to.be.an('array').and.have.lengthOf(1);
        expect(config[0]).to.be.instanceof(Server);
        expect(config[0].port).to.equal(80);
      }, done, done);
    });

    it('should handle the loading of certificate files', done => {
      let settings = `{
        "target": 3000,
        "ssl": {
          "cert": "test/fixtures/cert.pem",
          "key": "test/fixtures/key.pem"
        }
      }`;

      (new Application)._parseConfig(settings).subscribe(config => {
        expect(config).to.be.an('array').and.have.lengthOf(1);
        expect(config[0]).to.be.instanceof(Server);

        let cert = config[0]._options.ssl.cert;
        expect(cert).to.be.instanceof(Buffer);
        expect(cert.toString()).to.contain('-----BEGIN CERTIFICATE-----');

        let key = config[0]._options.ssl.key;
        expect(key).to.be.instanceof(Buffer);
        expect(key.toString()).to.contain('-----BEGIN ENCRYPTED PRIVATE KEY-----');
      }, done, done);
    });
  });
});

'use strict';

const {expect} = require('chai');
const {STATUS_CODES} = require('http');
const {Observable, Subject} = require('rxjs');
const {Server} = require('../lib');

/**
 * @test {Server}
 */
describe('Server', () => {

  /**
   * @test {Server#address}
   */
  describe('#address', () => {
    it('should have an "any IPv4" address as the default address', () => {
      expect((new Server).address).to.equal(Server.DEFAULT_ADDRESS);
    });

    it('should have the same host as the specified one', () => {
      expect(new Server({address: 'localhost'}).address).to.equal('localhost');
    });
  });

  /**
   * @test {Server#listening}
   */
  describe('#listening', () => {
    it('should return whether the server is listening', done => {
      let server = new Server({address: '127.0.0.1', port: 0});
      expect(server.listening).to.be.false;

      server.listen()
        .do(() => expect(server.listening).to.be.true)
        .mergeMap(() => server.close())
        .do(() => expect(server.listening).to.be.false)
        .subscribe(null, done, done);
    });
  });

  /**
   * @test {Server#onClose}
   */
  describe('#onClose', () => {
    it('should return an `Observable` instead of the underlying `Subject`', () => {
      let stream = new Server().onClose;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onError}
   */
  describe('#onError', () => {
    it('should return an `Observable` instead of the underlying `Subject`', () => {
      let stream = new Server().onError;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onListening}
   */
  describe('#onListening', () => {
    it('should return an `Observable` instead of the underlying `Subject`', () => {
      let stream = new Server().onListening;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#onRequest}
   */
  describe('#onRequest', () => {
    it('should return an `Observable` instead of the underlying `Subject`', () => {
      let stream = new Server().onRequest;
      expect(stream).to.be.instanceof(Observable);
      expect(stream).to.not.be.instanceof(Subject);
    });
  });

  /**
   * @test {Server#port}
   */
  describe('#port', () => {
    it('should have 3000 as the default port', () => {
      expect((new Server).port).to.equal(Server.DEFAULT_PORT);
    });

    it('should have the same port as the specified one', () => {
      expect(new Server({port: 8080}).port).to.equal(8080);
    });
  });

  /**
   * @test {Server#routes}
   */
  describe('#routes', () => {
    it('should be empty by default', () => {
      expect((new Server).routes.size).to.equal(0);
    });

    it('should create a default route if a target is specified', () => {
      let routes = new Server({target: 9000}).routes;
      expect(routes.size).to.equal(1);
      expect(routes.get('*')).to.be.an('object').and.have.property('uri');
    });

    it('should normalize the specified targets', () => {
      let routes = new Server({routes: {'belin.io': 9000}}).routes;
      expect(routes.get('belin.io')).to.be.an('object')
        .and.have.property('uri').that.equal('http://127.0.0.1:9000');
    });
  });

  /**
   * @test {Server#_getHostname}
   */
  describe('#_getHostname()', () => {
    it('it should return "*" if there is no "Host" header in the request', () => {
      expect((new Server)._getHostname({headers: {}})).to.equal('*');
    });

    it('it should return the "Host" header found in the request, without the port number', () => {
      expect((new Server)._getHostname({headers: {host: 'belin.io:8080'}})).to.equal('belin.io');
    });
  });

  /**
   * @test {Server#_normalizeRoute}
   */
  describe('#_normalizeRoute()', () => {
    it('it should normalize a port on the local host', () => {
      expect((new Server)._normalizeRoute(3000)).to.deep.equal({headers: {}, uri: 'http://127.0.0.1:3000'});
      expect((new Server)._normalizeRoute({uri: 3000})).to.deep.equal({headers: {}, uri: 'http://127.0.0.1:3000'});
    });

    it('it should normalize an authority', () => {
      expect((new Server)._normalizeRoute('domain.com:8080')).to.deep.equal({headers: {}, uri: 'http://domain.com:8080'});
      expect((new Server)._normalizeRoute({uri: 'domain.com:8080'})).to.deep.equal({headers: {}, uri: 'http://domain.com:8080'});
    });

    it('it should normalize an origin', () => {
      expect((new Server)._normalizeRoute('https://domain.com:8080')).to.deep.equal({headers: {}, uri: 'https://domain.com:8080'});
      expect((new Server)._normalizeRoute({uri: 'https://domain.com:8080'})).to.deep.equal({headers: {}, uri: 'https://domain.com:8080'});
    });

    it('it should normalize the HTTP headers', () => {
      let headers = {'X-Header': 'X-Value'};
